const items = $input.all();

// Skip first 4 items
return items.slice( $('Edit Fields1').first().json.page*$('Edit Fields1').first().json.page_size );

const items = $input.all();
const webhook = $('Webhook').first().json.query;

// If no items, return empty array
if (!items || items.length === 0) {
  return [];
}

// Apply all filters
const filtered = items.filter(item => {
  const job = item.json;

  // Status filter (always active)
  if (job.status !== 'active') return false;

  // Location filter
  if (webhook.location && job.location !== webhook.location) return false;

  // Employment type filter
  if (webhook.employment_type && job.employment_type !== webhook.employment_type) return false;

  // Experience level filter
  if (webhook.experience_level && job.experience_level !== webhook.experience_level) return false;

  // Remote filter
  if (webhook.is_remote !== undefined) {
    const isRemote = webhook.is_remote === 'true' || webhook.is_remote === true;
    if (job.is_remote !== isRemote) return false;
  }

  // Hybrid filter
  if (webhook.is_hybrid !== undefined) {
    const isHybrid = webhook.is_hybrid === 'true' || webhook.is_hybrid === true;
    if (job.is_hybrid !== isHybrid) return false;
  }

  // Salary filters
  if (webhook.salary_min && (!job.salary_min || job.salary_min < Number(webhook.salary_min))) return false;
  if (webhook.salary_max && (!job.salary_max || job.salary_max > Number(webhook.salary_max))) return false;

  // Currency filter
  if (webhook.salary_currency && job.salary_currency !== webhook.salary_currency) return false;

  // Company name filter
  if (webhook.company_name && !job.company_name.toLowerCase().includes(webhook.company_name.toLowerCase())) return false;

  // Skills filter
  if (webhook.required_skills) {
    const requiredSkills = webhook.required_skills.split(',').map(s => s.trim().toLowerCase());
    const jobSkills = (job.required_skills || []).map(s => s.toLowerCase());
    const hasAllSkills = requiredSkills.every(skill => 
      jobSkills.some(jobSkill => jobSkill.includes(skill))
    );
    if (!hasAllSkills) return false;
  }

  // Preferred skills filter
  if (webhook.preferred_skills) {
    const preferredSkills = webhook.preferred_skills.split(',').map(s => s.trim().toLowerCase());
    const jobSkills = (job.preferred_skills || []).map(s => s.toLowerCase());
    const hasSomeSkills = preferredSkills.some(skill => 
      jobSkills.some(jobSkill => jobSkill.includes(skill))
    );
    if (!hasSomeSkills) return false;
  }

  // Job category filter
  if (webhook.job_category && job.job_category !== webhook.job_category) return false;

  // Industry filter
  if (webhook.industry && job.industry !== webhook.industry) return false;

  // Company size filter
  if (webhook.company_size && job.company_size !== webhook.company_size) return false;

  // Education filter
  if (webhook.education_required && job.education_required !== webhook.education_required) return false;

  // Visa sponsorship filter
  if (webhook.visa_sponsorship !== undefined) {
    const needsVisa = webhook.visa_sponsorship === 'true' || webhook.visa_sponsorship === true;
    if (job.visa_sponsorship !== needsVisa) return false;
  }

  // Benefits filter
  if (webhook.benefits) {
    const requiredBenefits = webhook.benefits.split(',').map(b => b.trim());
    const jobBenefits = job.benefits || [];
    const hasAllBenefits = requiredBenefits.every(benefit => 
      jobBenefits.includes(benefit)
    );
    if (!hasAllBenefits) return false;
  }

  // Languages filter
  if (webhook.languages) {
    const requiredLanguages = webhook.languages.split(',').map(l => l.trim());
    const jobLanguages = job.languages || [];
    const hasAllLanguages = requiredLanguages.every(lang => 
      jobLanguages.includes(lang)
    );
    if (!hasAllLanguages) return false;
  }

  // Posted date filter
  if (webhook.posted_days_ago && job.created_at) {
    const daysAgo = (Date.now() - new Date(job.created_at).getTime()) / (1000 * 60 * 60 * 24);
    if (daysAgo > Number(webhook.posted_days_ago)) return false;
  }

  return true;
});

return filtered.length > 0 ? filtered : [];

const items = $input.all();
const webhook = $('Webhook').first().json.query;

// If no items, return empty array
if (!items || items.length === 0) {
  return [];
}

// Apply sorting if specified
if (webhook.sort_by) {
  const sortOrder = webhook.sort_order === 'desc' ? -1 : 1;

  items.sort((a, b) => {
    const jobA = a.json;
    const jobB = b.json;

    let comparison = 0;

    switch (webhook.sort_by) {
      case 'date':
        comparison = new Date(jobA.created_at) - new Date(jobB.created_at);
        break;
      case 'salary':
        const salaryA = jobA.salary_max || jobA.salary_min || 0;
        const salaryB = jobB.salary_max || jobB.salary_min || 0;
        comparison = salaryA - salaryB;
        break;
      case 'company':
        comparison = (jobA.company_name || '').localeCompare(jobB.company_name || '');
        break;
      case 'relevance':
      default:
        // Keep original order (relevance from vector search)
        return 0;
    }

    return comparison * sortOrder;
  });
}

return items;
